fun handleError(nameSpace, logErrCode, errDesc, userErrDesc, syserrDesc): Object=
{
	"traxion_response": {
		"completed_succesfully": "false",
		"error": {
			"error_type": nameSpace ++ ": " ++ logErrCode,
			"user_error_description": userErrDesc ++ errDesc,
			"system_error_description": syserrDesc
		}
	}
}

var nameSpace=vars.errorMessageTep.errorType.namespace
var logErrCode=(if (vars.errorMessageTep.errorMessage.payload.traxion_response.error.user_error_description contains "409") "409" 
	else if (vars.errorMessageTep.errorMessage.payload.traxion_response.error.user_error_description contains "404") "404"
	else "error": vars.errorMessageTep)
var errDesc=vars.errorMessageTep.errorMessage.payload.traxion_response.error.user_error_description
var userErrDesc="The request cannot be processed due to a conflict in the current state of the resource on the server. -> "
var syserrDesc=vars.errorMessageTep.errorMessage.payload.traxion_response.error.system_error_description.system_error_description.response

---

handleError(nameSpace, logErrCode, errDesc, userErrDesc, syserrDesc)

